--
-- Table structure for table `places`
--

CREATE TABLE IF NOT EXISTS `places` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` text COLLATE utf8_unicode_ci NOT NULL,
  `feature_img` text COLLATE utf8_unicode_ci NOT NULL,
  `description` text COLLATE utf8_unicode_ci NOT NULL,
  `content` text COLLATE utf8_unicode_ci NOT NULL,
  `destination` text COLLATE utf8_unicode_ci NOT NULL,
  `time` float NOT NULL,
  `cost` float NOT NULL,
  `quality` float NOT NULL,
  `nature` text COLLATE utf8_unicode_ci NOT NULL,
  `religious` text COLLATE utf8_unicode_ci NOT NULL,
  `adventure` text COLLATE utf8_unicode_ci NOT NULL,
  `health_or_medical` text COLLATE utf8_unicode_ci NOT NULL,
  `latitude` float NOT NULL,
  `longitude` float NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=18 ;

--
-- Dumping data for table `places`
--

INSERT INTO `places` (`id`, `title`, `feature_img`, `description`, `content`, `destination`, `time`, `cost`, `quality`, `nature`, `religious`, `adventure`, `health_or_medical`, `latitude`, `longitude`) VALUES
(13, 'Cô Tô – đảo nhỏ giữa xanh ngắt đại dương', 'http://localhost/img_dl/hoang-hon-co-to.jpg', 'Cô Tô – đảo nhỏ giữa xanh ngắt đại dương', '<p><img class="alignnone size-full wp-image-4995" src="http://localhost/img_dl/hoang-hon-co-to.jpg" sizes="(max-width: 2048px) 100vw, 2048px" srcset="http://dulichvietnamhanoi.com/wp-content/uploads/2010/11/hoang-hon-co-to.jpg 2048w, http://dulichvietnamhanoi.com/wp-content/uploads/2010/11/hoang-hon-co-to-300x189.jpg 300w, http://dulichvietnamhanoi.com/wp-content/uploads/2010/11/hoang-hon-co-to-1024x644.jpg 1024w" alt="hoang-hon-co-to" width="700" height="440" /></p>\r\n<p><em>C&ocirc;</em><em>T&ocirc; &ndash; thi&ecirc;n đường nghỉ dưỡng thực sự cho du kh&aacute;ch</em></p>\r\n<p>Nếu bạn ở H&agrave; Nội th&igrave; 5h30 chiều c&oacute; thể bắt xe H&ograve;n Gai, chuyến cuối ở bến Lương Y&ecirc;n (gi&aacute; v&eacute;: Khoảng 50 ngh&igrave;n đồng/kh&aacute;ch) để bắt đầu cuộc h&agrave;nh tr&igrave;nh tới đảo C&ocirc; T&ocirc; (Quảng Ninh). Ngủ đ&ecirc;m tại V&acirc;n Đồn, h&ograve;a m&igrave;nh với những những g&igrave; c&ograve;n sơ khai nhất của tạo h&oacute;a c&ugrave;ng h&agrave;ng ng&agrave;n k&igrave; quan thi&ecirc;n tạo, nhấm nh&aacute;p những m&oacute;n đặc sản của miền biển, để qu&ecirc;n hết đi những bộn bề của cuộc sống mưu sinh. S&aacute;ng ra 6h30, l&ecirc;n chuyến t&agrave;u ra đảo C&ocirc; T&ocirc;.</p>\r\n<p>&nbsp;&nbsp;Sau gần 3h đi bằng t&agrave;u du lịch, C&ocirc; T&ocirc; m&ecirc; hoặc du kh&aacute;ch bởi những b&atilde;i biển hoang sơ với bờ c&aacute;t trắng v&agrave; l&agrave;n nước trong xanh. Nhiều người lại ấn tượng với bữa tiệc nướng b&ecirc;n biển, hay l&ecirc;nh đ&ecirc;nh tr&ecirc;n con thuyền c&acirc;u mực v&agrave;o ban đ&ecirc;m, ch&egrave;o l&ecirc;n ngọn hải đăng ngắm nh&igrave;n bốn bề trời m&acirc;y s&ocirc;ng nước hoặc ngủ ngon l&agrave;nh trong những căn lều du lịch để cảm nhận được c&aacute;t lạo xạo dưới lưng.</p>\r\n<p>Tuy kh&ocirc;ng c&oacute; nhiều điểm để vui chơi nhưng C&ocirc; T&ocirc; &ndash; h&ograve;n đảo nhỏ giữa xanh ngắt đại dương n&agrave;y xứng đ&aacute;ng l&agrave; điểm nghỉ ngơi l&yacute; tưởng cho du kh&aacute;ch.</p>\r\n<p>Gi&aacute; nh&agrave; nghỉ khoảng 150 &ndash; 200.000 đồng/ph&ograve;ng đ&ocirc;i. Chi ph&iacute; dự tr&ugrave; cho chuyến đi 3 ng&agrave;y khoảng 950.000 đồng/người</p>', 'Quảng ninh', 24, 1000000, 3, 'Rất nhiều', 'Không có', 'Khá nhiều', 'Khá nhiều', 20.9973, 107.77),
(14, 'Cù Lao Chàm – viên ngọc xanh', 'http://localhost/img_dl/cu-lao-cham-bai-lang.jpg', 'Cù Lao Chàm – viên ngọc xanh', '<p><img class="alignnone size-full wp-image-4996" src="http://localhost/img_dl/cu-lao-cham-bai-lang.jpg" sizes="(max-width: 800px) 100vw, 800px" srcset="http://dulichvietnamhanoi.com/wp-content/uploads/2010/11/cu-lao-cham-bai-lang.jpg 800w, http://dulichvietnamhanoi.com/wp-content/uploads/2010/11/cu-lao-cham-bai-lang-300x150.jpg 300w" alt="cu-lao-cham-bai-lang" width="800" height="400" /></p>\r\n<p>&nbsp;Nhiều người v&iacute; C&ugrave; Lao Ch&agrave;m như một vi&ecirc;n đ&aacute; chưa được gọt giũa, c&aacute;c cạnh của n&oacute; vẫn nguy&ecirc;n vẻ x&ugrave; x&igrave;, ngăn trở bằng những b&atilde;i đ&aacute; nhấp nh&ocirc; như răng khểnh. Từ Cửa Đại, Hội An, chỉ mất tầm 20 ph&uacute;t đi tr&ecirc;n những con s&oacute;ng, người ta c&oacute; thể đặt ch&acirc;n l&ecirc;n b&atilde;i c&aacute;t trắng hoang sơ của một g&oacute;c thi&ecirc;n đường C&ugrave; Lao Ch&agrave;m. Với 8 h&ograve;n đảo nhỏ trải d&agrave;i theo h&igrave;nh c&aacute;nh cung xanh mướt, C&ugrave; Lao Ch&agrave;m l&agrave; một trong nhiều quần đảo hoang sơ mới được đưa v&agrave;o khai th&aacute;c trong v&agrave;i năm gần đ&acirc;y ở Quảng Nam.</p>\r\n<p>Ở đ&oacute;, ban đ&ecirc;m kh&ocirc;ng c&oacute; điện, mọi người đốt lửa trại ngắm nh&igrave;n biển đ&ecirc;m dưới bầu trời với h&agrave;ng ng&agrave;n v&igrave; sao lấp l&aacute;nh. S&aacute;ng ra, những con s&oacute;ng đập tận v&agrave;o ch&acirc;n như đ&aacute;nh thức bạn dậy đ&oacute;n ch&agrave;o b&igrave;nh minh rực rỡ tr&ecirc;n biển. Bạn c&oacute; thể thu&ecirc; thuyền ngắm san h&ocirc; hoặc đi một v&ograve;ng xem tổ chim yến, đặc biệt l&agrave; tận mắt chứng kiến h&agrave;ng trăm con cua biển b&ograve; lồm ngồm tr&ecirc;n những tảng đ&aacute; trước mặt.</p>\r\n<p>&nbsp;Việc đặt v&eacute; m&aacute;y bay v&agrave;o thời kỳ cao điểm như 30/4 dường như rất kh&oacute; khăn. V&igrave; vậy, để tiết kiệm chi ph&iacute; đi lại, bạn c&oacute; thể đi xe chất lượng cao v&agrave;o Đ&agrave; Nẵng, ngủ một đ&ecirc;m ở kh&aacute;ch sạn gần s&ocirc;ng H&agrave;n, thả bộ tr&ecirc;n con đường l&atilde;ng mạn, thơ mộng đẹp nhất nơi đ&acirc;y (gi&aacute; ph&ograve;ng từ 100 &ndash; 200 ngh&igrave;n đồng). S&aacute;ng h&ocirc;m sau, du kh&aacute;ch c&oacute; thể bắt xe &ocirc; t&ocirc; hoặc xe bus về Hội An đắm m&igrave;nh nơi những căn nh&agrave; r&ecirc;u phong phố cổ v&agrave; ngụp lặn dưới l&agrave;n nước trong xanh v&agrave; nằm tắm nắng tr&ecirc;n những b&atilde;i c&aacute;t trắng phẳng l&igrave;.</p>\r\n<p>&nbsp;Chi ph&iacute; dự t&iacute;nh thoải m&aacute;i cho việc ăn ở + vui chơi khoảng 400.000 đồng/người/ng&agrave;y.</p>', 'Quảng nam', 24, 400000, 2, 'Rất nhiều', 'Khá ít', 'Khá nhiều', 'Tương đối', 15.9593, 108.513),
(15, 'Cà Mau – tận hưởng cảm giác mới', 'http://localhost/img_dl/ca-mau.jpg', 'Cà Mau – tận hưởng cảm giác mới', '<p><img class="alignnone size-full wp-image-4997" src="http://localhost/img_dl/ca-mau.jpg" sizes="(max-width: 1064px) 100vw, 1064px" srcset="http://dulichvietnamhanoi.com/wp-content/uploads/2010/11/ca-mau.jpg 1064w, http://dulichvietnamhanoi.com/wp-content/uploads/2010/11/ca-mau-300x169.jpg 300w, http://dulichvietnamhanoi.com/wp-content/uploads/2010/11/ca-mau-1024x578.jpg 1024w" alt="ca mau" width="800" height="452" /></p>\r\n<p>&nbsp;Địa điểm n&agrave;y th&iacute;ch hợp cho những cư d&acirc;n sinh sống tại miền Nam. Mảnh đất n&agrave;y chỉ c&aacute;ch Tp. Hồ Ch&iacute; Minh chưa đầy một giờ bay. Du kh&aacute;ch cũng c&oacute; thể đi bằng xe của Mai Linh bất cứ l&uacute;c n&agrave;o.</p>\r\n<p>Mỗi lần ngồi ca n&ocirc; từ Tp. C&agrave; Mau xu&ocirc;i về Đất Mũi sẽ đều đem đến cho bạn những trải nghiệm kh&oacute; qu&ecirc;n. Tận hưởng cảm gi&aacute;c ch&ograve;ng ch&agrave;nh tr&ecirc;n s&ocirc;ng nước v&agrave; mục k&iacute;ch cuộc sống s&ocirc;i động hai b&ecirc;n bờ s&ocirc;ng, khiến hai giờ đồng hồ từ th&agrave;nh phố C&agrave; Mau đến Đất Mũi chỉ như tho&aacute;ng chốc. Du kh&aacute;ch sẽ thỏa sức h&ograve;a m&igrave;nh c&ugrave;ng biển Khai Long khi c&ugrave;ng người d&acirc;n đi v&aacute;n trượt b&ugrave;n bắt s&ograve; huyết. Một m&oacute;n ăn bổ dưỡng v&agrave; đậm đ&agrave; hương vị ph&ugrave; sa.</p>\r\n<p>C&agrave; Mau c&ograve;n tự h&agrave;o khi được tạo h&oacute;a ban tặng khu rừng U Minh hạ tr&ugrave;ng điệp, với những h&agrave;ng c&acirc;y mắm ti&ecirc;n phong ng&agrave;y đ&ecirc;m lấn biển bồi đắp cho đất Việt th&ecirc;m d&agrave;i, th&ecirc;m rộng. Đứng tr&ecirc;n Vọng l&acirc;m đ&agrave;i, ph&oacute;ng tầm mắt hướng về v&ugrave;ng biển bao la với từng đợt s&oacute;ng nhấp nh&ocirc; b&ecirc;n những mạn thuyền đầy ắp c&aacute; t&ocirc;m đang dần cặp bến, những d&atilde;y nh&agrave; nghỉ nằm ẩn m&igrave;nh trong rừng tạo cho Đất Mũi một địa thế l&acirc;m thủy hữu t&igrave;nh.</p>\r\n<p>Chi ph&iacute; trọn g&oacute;i dự t&iacute;nh cho chuyến đi C&agrave; Mau khoảng gần 1 triệu đồng/ người/ 3 ng&agrave;y 2 đ&ecirc;m.</p>', 'Cà Mau', 60, 1000000, 3, 'Tương đối', 'Tương đối', 'Khá nhiều', 'Tương đối', 9.15091, 105.202),
(16, 'Mộc Châu – Thảo nguyên hoa', 'http://localhost/img-dl/moc-chau.jpg', 'Mộc Châu – Thảo nguyên hoa', '<p><img class="alignnone size-full wp-image-4998" src="http://localhost/img-dl/moc-chau.jpg" sizes="(max-width: 1280px) 100vw, 1280px" srcset="http://dulichvietnamhanoi.com/wp-content/uploads/2010/11/moc-chau.jpg 1280w, http://dulichvietnamhanoi.com/wp-content/uploads/2010/11/moc-chau-300x169.jpg 300w, http://dulichvietnamhanoi.com/wp-content/uploads/2010/11/moc-chau-1024x576.jpg 1024w" alt="moc chau" width="800" height="450" /></p>\r\n<p>Người ta đến với Mộc Ch&acirc;u để nghỉ ngơi sau mỗi dịp cuối tuần. Lang thang tr&ecirc;n những đồi ch&egrave; cũng đang nở hoa, ngắm bầu trời xanh trong lồng lộng từ tr&ecirc;n cao, nh&oacute;n ch&acirc;n bước qua những phiến đ&aacute; r&ecirc;u phong của th&aacute;c Dải Yếm, t&ograve; m&ograve; với Hang Dơi, hay đơn giản chỉ l&agrave; nhấm nh&aacute;p ch&uacute;t hương vị m&aacute;t lạnh mỗi sớm sương chưa tan vẫn giăng mắc khắp c&aacute;c hang c&ugrave;ng ng&otilde; hẻm của mảnh đất n&agrave;y.</p>\r\n<p>C&oacute; thể n&oacute;i, kh&ocirc;ng kh&iacute; tại Mộc Ch&acirc;u l&agrave; một trong những m&oacute;n qu&agrave; hiếm c&oacute; v&agrave; qu&yacute; gi&aacute; nhất của v&ugrave;ng đất n&agrave;y. Khu cao nguy&ecirc;n m&aacute;t mẻ v&agrave;o m&ugrave;a h&egrave;, se se lạnh m&ugrave;a thu, lạnh c&oacute;ng v&agrave;o m&ugrave;a đ&ocirc;ng v&agrave; ấm &aacute;p khi xu&acirc;n về đ&atilde; kh&ocirc;ng c&ograve;n l&agrave; c&aacute;i t&ecirc;n xa lạ với những người bạn đường khi m&ugrave;a hoa về.</p>\r\n<p>Nhiều đ&ocirc;i bạn gh&eacute; đến đ&acirc;y chụp ảnh cưới, c&oacute; người chỉ để thư gi&atilde;n cuối tuần, tự thưởng cho m&igrave;nh một bữa s&aacute;ng nhẹ nh&agrave;ng với ly sữa tươi n&oacute;ng hổi nguy&ecirc;n chất v&agrave; chiếc b&aacute;nh sừng b&ograve; thơm thơm gi&aacute; rẻ, chụp cho nhau những tấm ảnh đẹp trong khung trời đầy sắc hoa trong những vườn cải trắng, vườn hoa hướng dương, vườn lan, b&ecirc;n c&acirc;y hồng l&uacute;c lỉu quả ch&iacute;n mọng hay tr&ecirc;n đồi ch&egrave; cao t&iacute;t tắp xanh mướt xa xa&hellip;</p>\r\n<p>&nbsp;Nếu đi từ H&agrave; Nội, bạn c&oacute; thể bắt xe &ocirc; t&ocirc; tuyến H&agrave; Nội &ndash; Sơn la (Tại bến xe H&agrave; Đ&ocirc;ng, gi&aacute; tham khảo l&agrave; 115.000 đồng). Gi&aacute; ph&ograve;ng tại kh&aacute;ch sạn C&ocirc;ng Đo&agrave;n &ndash; n&ocirc;ng trường Mộc Ch&acirc;u khoảng 250.000 đồng/ph&ograve;ng hai giường. Chi ph&iacute; tổng cộng tối thiểu cho một chuyến đi Mộc Ch&acirc;u khoảng 1.00.000 &ndash; 1.200.000 đ/người/3 ng&agrave;y.</p>', 'Sơn La', 72, 1200000, 3, 'Rất nhiều', 'Tương đối', 'Rất nhiều', 'Tương đối', 21.1767, 104.68),
(17, 'Hồ Ba Bể – Thác Bản Giốc', 'http://localhost/img_dl/ho-ba-be.jpg', 'Hồ Ba Bể – Thác Bản Giốc', '<p>&nbsp;<img class="alignnone size-full wp-image-4999" src="http://localhost/img_dl/ho-ba-be.jpg" sizes="(max-width: 1024px) 100vw, 1024px" srcset="http://dulichvietnamhanoi.com/wp-content/uploads/2010/11/ho-ba-be.jpg 1024w, http://dulichvietnamhanoi.com/wp-content/uploads/2010/11/ho-ba-be-300x113.jpg 300w" alt="ho ba be" width="800" height="301" /></p>\r\n<p>Người xưa gọi hồ Ba Bể (tỉnh Bắc Kạn) l&agrave; &ldquo;Thi&ecirc;n nhi&ecirc;n đệ nhất hồ&rdquo;. Thu&ecirc; thuyền độc mộc l&ecirc;nh đ&ecirc;nh giữa đ&ecirc;m tr&ecirc;n hồ Ba Bể mới l&agrave; điều kh&aacute;c lạ v&agrave; kỳ diệu. Trong hơi men của rượu, vị tanh tanh của c&aacute; nướng, v&agrave; vị ngọt đầu m&ocirc;i của&hellip; b&aacute;nh b&iacute;ch quy, mang đi để nhấm nh&aacute;p tr&ecirc;n thuyền, du kh&aacute;ch sẽ cảm nhận được những kh&aacute;c biệt của phong cảnh nơi đ&acirc;y.</p>\r\n<p>So với Vịnh Hạ Long, nơi c&oacute; n&uacute;i tr&ecirc;n biển th&igrave; ở đ&acirc;y, Ba Bể lại c&oacute; biển tr&ecirc;n n&uacute;i. Đ&oacute; l&agrave; điều kh&aacute;c biệt, độc đ&aacute;o, kh&ocirc;ng nơi n&agrave;o c&oacute;.</p>\r\n<p>L&ecirc;nh đ&ecirc;nh tr&ecirc;n những chiếc thuyền độc mộc tr&ocirc;i theo hồ Ba Bể đem đến cho du kh&aacute;ch những cảm gi&aacute;c mới lạ</p>\r\n<p>Ba Bể c&aacute;ch H&agrave; Nội 300km, c&aacute;ch thị x&atilde; Bắc Cạn 50km.Từ H&agrave; Nội c&oacute; thể đi xe m&aacute;y hoặc đ&oacute;n xe kh&aacute;ch đi Bắc Cạn (bến Gia L&acirc;m) tiếp đ&oacute; đ&oacute;n xe đi thị trấn Chợ R&atilde; đi xe &ocirc;m v&agrave;o Ba Bể với gi&aacute; từ 20.000đ đến 30.000đ. Kh&aacute;ch c&oacute; thể ăn nghỉ tại thị trấn, gi&aacute; ph&ograve;ng từ 100.000- 150.000 đồng/ ph&ograve;ng đ&ocirc;i hoặc nghỉ tại nh&agrave; kh&aacute;ch của Vườn quốc gia Ba Bể gi&aacute; từ 150.000 &ndash; 200.000 đồng/ ph&ograve;ng. Kh&aacute;ch cũng c&oacute; thể nghỉ trọ tại c&aacute;c nh&agrave; s&agrave;n người T&agrave;y tại P&aacute;c Ng&ograve;i, P&oacute; L&ugrave; với gi&aacute; 20.000 &ndash; 50.000 đồng/ người (ngủ gh&eacute;p chung).</p>\r\n<p>Sau khi nghỉ đ&ecirc;m tại Cao Bằng, s&aacute;ng h&ocirc;m sau, du kh&aacute;ch c&oacute; thể đi thăm Th&aacute;c Bản Giốc &ndash; ngọn th&aacute;c đẹp nhất, h&ugrave;ng vĩ nhất Việt Nam nằm tr&ecirc;n đường bi&ecirc;n giới Việt Trung. V&agrave;o những h&ocirc;m trời nắng, &aacute;nh nắng chiếu v&agrave;o d&ograve;ng th&aacute;c qua l&agrave;n nước bụi m&ugrave; mịt tạo n&ecirc;n những chiếc cầu vồng n&ecirc;n thơ, lung linh, huyền ảo.</p>\r\n<p>Chi ph&iacute; dự kiến cho chuyến đi tối thiểu khoảng 550.000 đồng/ người/ 3 ng&agrave;y.</p>', 'Bắc Kạn', 550000, 72, 1, 'Rất nhiều', 'Khá ít', 'Rất nhiều', 'Không có', 22.4271, 105.614);

